import React, { useState } from 'react';

function TodoItem({ todo, toggleComplete, deleteTodo, editTodo }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editText, setEditText] = useState(todo.text);

  const handleEdit = () => {
    setIsEditing(true);
    setEditText(todo.text);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditText(todo.text);
  };

  const handleSave = () => {
    if (editText.trim()) {
      editTodo(todo.id, editText);
      setIsEditing(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSave();
    } else if (e.key === 'Escape') {
      handleCancel();
    }
  };

  return (
    <div className="todo-item">
      {isEditing ? (
        // 編輯模式
        <div className="edit-mode">
          <input
            type="text"
            value={editText}
            onChange={(e) => setEditText(e.target.value)}
            onKeyDown={handleKeyDown}
            autoFocus
            className="edit-input"
          />
          <div className="edit-buttons">
            <button onClick={handleSave} className="save-button">儲存</button>
            <button onClick={handleCancel} className="cancel-button">取消</button>
          </div>
        </div>
      ) : (
        // 正常顯示模式
        <>
          <input 
            type="checkbox" 
            checked={todo.completed} 
            onChange={() => toggleComplete(todo.id)} 
          />
          <span 
            className={todo.completed ? 'completed' : ''}
            onClick={() => toggleComplete(todo.id)}
          >
            {todo.text}
          </span>
          <div className="action-buttons">
            <button onClick={handleEdit} className="edit-button">編輯</button>
            <button onClick={() => deleteTodo(todo.id)} className="delete-button">刪除</button>
          </div>
        </>
      )}
    </div>
  );
}

export default TodoItem;
