import { createClient } from '@supabase/supabase-js';

// 獲取環境變數
const supabaseUrl = process.env.REACT_APP_SUPABASE_URL || '';
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY || '';

console.log('Supabase URL:', supabaseUrl);
console.log('Supabase Key:', supabaseAnonKey ? 'Key exists but hidden for security' : 'Key not found');

if (!supabaseUrl || !supabaseAnonKey) {
  console.error('環境變數未設定: REACT_APP_SUPABASE_URL 或 REACT_APP_SUPABASE_ANON_KEY 未在 .env.local 檔案中找到或未正確載入');
}

// 使用硬編碼的後備值 (僅用於測試，建議總是使用環境變數)
const finalSupabaseUrl = supabaseUrl || 'https://icokkqjoihfftbunwlae.supabase.co';
const finalSupabaseAnonKey = supabaseAnonKey || 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Imljb2trcWpvaWhmZnRidW53bGFlIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NDQ3MDc4MzYsImV4cCI6MjA2MDI4MzgzNn0.noQKkvJfTKbshVr28s9uoRwn1EbmJYlxyVbBTyzokCg';

// 創建 Supabase 客戶端
let supabase = null;
try {
  supabase = createClient(finalSupabaseUrl, finalSupabaseAnonKey);
  console.log('Supabase 客戶端創建成功');
} catch (error) {
  console.error('創建 Supabase 客戶端失敗:', error.message);
}

export { supabase };
