import React from 'react';
import TodoItem from './TodoItem';

function TodoList({ todos, toggleComplete, deleteTodo, editTodo, isLoading }) {
  if (isLoading && todos.length === 0) {
    return <div className="loading-message">載入待辦事項中...</div>;
  }

  return (
    <div className="todo-list">
      {todos.length === 0 ? (
        <p className="empty-message">目前沒有待辦事項</p>
      ) : (
        todos.map(todo => (
          <TodoItem 
            key={todo.id} 
            todo={todo} 
            toggleComplete={toggleComplete} 
            deleteTodo={deleteTodo} 
            editTodo={editTodo}
          />
        ))
      )}
    </div>
  );
}

export default TodoList;
