import React, { useState, useEffect } from 'react';
import TodoList from './components/TodoList';
import AddTodoForm from './components/AddTodoForm';
import './App.css';
import { supabase } from './supabaseClient';

function App() {
  const [todos, setTodos] = useState([]);
  const [connectionStatus, setConnectionStatus] = useState('未測試');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // 從 Supabase 加載待辦事項
  useEffect(() => {
    fetchTodos();
  }, []);

  // 獲取所有待辦事項
  const fetchTodos = async () => {
    try {
      setIsLoading(true);
      const { data, error } = await supabase
        .from('todos')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) {
        throw error;
      }

      setTodos(data || []);
      console.log('成功獲取待辦事項:', data);
    } catch (error) {
      console.error('獲取待辦事項失敗:', error.message);
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // 新增待辦事項
  const addTodo = async (text) => {
    try {
      setIsLoading(true);
      const newTodo = {
        text,
        completed: false,
        user_id: 'anonymous', // 可以替換為實際使用者 ID
      };

      const { data, error } = await supabase
        .from('todos')
        .insert([newTodo])
        .select();

      if (error) {
        throw error;
      }

      console.log('成功新增待辦事項:', data);
      setTodos([...data, ...todos]);
      
    } catch (error) {
      console.error('新增待辦事項失敗:', error.message);
      alert('新增待辦事項失敗: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // 切換待辦事項完成狀態
  const toggleComplete = async (id) => {
    try {
      // 找出要更新的項目
      const todoToUpdate = todos.find(todo => todo.id === id);
      if (!todoToUpdate) return;

      // 先樂觀更新 UI
      setTodos(todos.map(todo => 
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      ));

      // 然後更新資料庫
      const { data, error } = await supabase
        .from('todos')
        .update({ completed: !todoToUpdate.completed })
        .eq('id', id)
        .select();

      if (error) {
        throw error;
      }

      console.log('成功更新待辦事項狀態:', data);
    } catch (error) {
      console.error('更新待辦事項狀態失敗:', error.message);
      
      // 如果資料庫更新失敗，回滾 UI 更新
      fetchTodos();
    }
  };

  // 刪除待辦事項
  const deleteTodo = async (id) => {
    try {
      // 先樂觀更新 UI
      setTodos(todos.filter(todo => todo.id !== id));

      // 然後從資料庫刪除
      const { error } = await supabase
        .from('todos')
        .delete()
        .eq('id', id);

      if (error) {
        throw error;
      }

      console.log('成功刪除待辦事項');
    } catch (error) {
      console.error('刪除待辦事項失敗:', error.message);
      
      // 如果資料庫刪除失敗，重新獲取數據
      fetchTodos();
    }
  };

  // 測試 Supabase 連接
  const testConnection = async () => {
    setIsLoading(true);
    try {
      // 嘗試從 Supabase 獲取數據
      const { data, error } = await supabase
        .from('todos')
        .select('count')
        .limit(1);
      
      if (error) {
        console.error('連接測試失敗:', error);
        setConnectionStatus(`連接失敗: ${error.message}`);
      } else {
        console.log('連接測試成功:', data);
        setConnectionStatus('連接成功！資料庫可用');
      }
    } catch (error) {
      console.error('連接測試失敗:', error.message);
      setConnectionStatus(`連接失敗: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app">
      <h1>待辦事項清單</h1>
      
      {/* Supabase 連接測試部分 */}
      <div className="connection-test">
        <h3>Supabase 連接狀態: <span className={connectionStatus.includes('成功') ? 'success' : 'failed'}>{connectionStatus}</span></h3>
        <button 
          onClick={testConnection} 
          disabled={isLoading}
          className="test-button"
        >
          {isLoading ? '測試中...' : '測試資料庫連接'}
        </button>
      </div>
      
      <AddTodoForm addTodo={addTodo} isLoading={isLoading} />
      
      {error && <div className="error-message">錯誤: {error}</div>}
      
      {isLoading && todos.length === 0 ? (
        <div className="loading">載入中...</div>
      ) : (
        <TodoList 
          todos={todos} 
          toggleComplete={toggleComplete} 
          deleteTodo={deleteTodo} 
          isLoading={isLoading}
        />
      )}
    </div>
  );
}

export default App;
